#include <iostream> #include <vector> #include <queue> #include <utility> #include <limits> using std :: cout ; using std :: endl ; using std :: vector ; using std :: pair ; using std :: priority_queue ; using std :: make_pair ; using ll = long long ; const ll INF = std :: numeric_limits < ll >:: max () / 4 ; // Dijkstra: returns distance vector from src to all nodes (0..n-1) // adj: adjacency list where adj[u] contains pairs (v, weight) vector < ll > dijkstra ( int n , int src , const vector < vector < pair < int , int >>> & adj ) { vector < ll > dist ( n , INF ); dist [ src ] = 0 ; // min-heap of (distance, node) priority_queue < pair < ll , int > , vector < pair < ll , int >> , std :: greater < pair < ll , int >>> pq ; pq . push ( make_pair ( 0L L , src )); while ( ! pq . empty ()) { auto [ d , u ] = pq . top (); pq . pop (); if ( d != dist [ u ]) continue ; // stale entry for ( const auto & e : adj [ u ]) { int v = e . first ; int w = e . second ; if ( dist [ v ] > dist [ u ] + w ) { dist [ v ] = dist [ u ] + w ; pq . push ( make_pair ( dist [ v ], v )); } } } return dist ; } int main () { // simple test graph // 6 nodes: 0..5 int n = 6 ; vector < vector < pair < int , int >>> adj ( n ); auto add_edge = [ & ]( int u , int v , int w ) { adj [ u ]. push_back ( make_pair ( v , w )); // if undirected, also add adj[v].push_back({u,w}); }; add_edge ( 0 , 1 , 7 ); add_edge ( 0 , 2 , 9 ); add_edge ( 0 , 5 , 14 ); add_edge ( 1 , 2 , 10 ); add_edge ( 1 , 3 , 15 ); add_edge ( 2 , 3 , 11 ); add_edge ( 2 , 5 , 2 ); add_edge ( 3 , 4 , 6 ); add_edge ( 4 , 5 , 9 ); int src = 0 ; auto dist = dijkstra ( n , src , adj ); cout << "Distances from node " << src << ": 
 " ; for ( int i = 0 ; i < n ; ++ i ) { if ( dist [ i ] >= INF / 2 ) cout << i << ": INF 
 " ; else cout << i << ": " << dist [ i ] << '
' ; } return 0 ; }
